INSERT INTO `wp_posts` VALUES('83199', '1', '2018-04-11 18:26:18', '2018-04-11 15:26:18', '[vc_row height=\"full\" valign=\"center\" width=\"full\" color_scheme=\"secondary\" us_bg_image=\"83059\" us_bg_repeat=\"no-repeat\" us_bg_parallax=\"still\" bg_type=\"bg_color\" bg_override=\"ex-full\" ult_hide_row=\"off\" css=\".vc_custom_1522603951450{padding-bottom: 70px !important;}\" el_id=\"home\"][vc_column el_class=\"align_center\"][vc_custom_heading text=\"<strong>Дешевый интернет в роуминге\r\nза границей</strong>\" font_container=\"tag:h1|text_align:center\" use_theme_fonts=\"yes\"][vc_custom_heading text=\"Купить сим-карту Orange в России <strong>за 690 руб.</strong>\" font_container=\"tag:h3|text_align:center\" use_theme_fonts=\"yes\"][us_separator][vc_row_inner columns_type=\"boxes\" gap=\"30\" el_id=\"our-advantages\"][vc_column_inner text_color=\"#353c3f\" width=\"1/3\"][vc_raw_html el_class=\"card-border-line gradient-secondary\"][/vc_raw_html][us_iconbox icon=\"material|sim_card\" color=\"custom\" style=\"outlined\" icon_color=\"#4061e9\" bg_color=\"#5e71b3\" size=\"1.9rem\" title=\"Самая низкая цена сим-карты\" img=\"1314\"]\r\n<p class=\"my-sep\">Сим-карта Orange за 690 руб.</p>\r\n<p class=\"my-sep\">Пополняем баланс сим-карт</p>\r\n[/us_iconbox][/vc_column_inner][vc_column_inner text_color=\"#353c3f\" width=\"1/3\"][vc_raw_html el_class=\"card-border-line gradient-secondary\"][/vc_raw_html][us_iconbox icon=\"\" color=\"custom\" style=\"outlined\" icon_color=\"#4061e9\" bg_color=\"#5e71b3\" size=\"1.9rem\" title=\"Самая низкая стоимость пакетов\" img=\"1315\"]\r\n<p class=\"my-sep\">Интернет 30 коп/Мб с 3G и <strong style=\"color: #e48400;\">4G</strong> интернетом</p>\r\n<p class=\"my-sep\">Входящие бесплатно</p>\r\n<p class=\"my-sep\">Единые настройки</p>\r\n<p class=\"my-sep\">Сим-карта бессрочная, при своевременном пополнении</p>\r\n[/us_iconbox][/vc_column_inner][vc_column_inner text_color=\"#353c3f\" width=\"1/3\"][vc_raw_html el_class=\"card-border-line gradient-secondary\"][/vc_raw_html][us_iconbox color=\"custom\" style=\"outlined\" icon_color=\"#4061e9\" bg_color=\"#5e71b3\" size=\"1.9rem\" title=\"Только у нас бесплатная доставка\" img=\"1316\"]\r\n<p class=\"my-sep\">Точки выдачи – 0 руб</p>\r\n<p class=\"my-sep\">Почта РФ – 0 руб</p>\r\n<p class=\"my-sep\">Курьерская служба от 300 руб.</p>\r\n<p class=\"my-sep\">Возможна оплата заказа после получения</p>\r\n[/us_iconbox][/vc_column_inner][/vc_row_inner][us_separator][us_btn text=\"Купить сим-карту \" link=\"url:%23prices|||\" color=\"secondary\" size=\"16px\" align=\"center\"][us_separator][vc_column_text el_class=\"about-roaming\"]Теперь не надо искать и покупать местные сим-карты в своих путешествиях.\r\n\r\nСим-карта Orange от ведущего европейского оператора с 1 июля 2017 г  упразднила роуминг в Европе.\r\n\r\nИнтернет-пакеты действуют во всех странах Евросоюза.[/vc_column_text][/vc_column][/vc_row][vc_row height=\"auto\" width=\"full\" color_scheme=\"secondary\" bg_type=\"\" parallax_style=\"\" bg_image_new=\"\" bg_image_repeat=\"\" bg_image_size=\"\" bg_cstm_size=\"\" bg_img_attach=\"\" parallax_sense=\"\" bg_image_posiiton=\"\" animation_direction=\"\" animation_repeat=\"\" video_url=\"\" video_url_2=\"\" u_video_url=\"\" video_poster=\"\" u_start_time=\"\" u_stop_time=\"\" viewport_vdo=\"\" enable_controls=\"\" bg_override=\"\" disable_on_mobile_img_parallax=\"\" parallax_content=\"\" parallax_content_sense=\"\" fadeout_row=\"\" fadeout_start_effect=\"\" enable_overlay=\"\" overlay_color=\"\" overlay_pattern=\"\" overlay_pattern_opacity=\"\" overlay_pattern_size=\"\" overlay_pattern_attachment=\"\" seperator_enable=\"\" seperator_type=\"\" seperator_position=\"\" seperator_shape_size=\"\" seperator_svg_height=\"\" seperator_shape_background=\"\" seperator_shape_border=\"\" seperator_shape_border_color=\"\" seperator_shape_border_width=\"\" icon_type=\"\" icon=\"\" icon_size=\"\" icon_color=\"\" icon_style=\"\" icon_color_bg=\"\" icon_border_style=\"\" icon_border_spacing=\"\" icon_img=\"\" img_width=\"\" ult_hide_row=\"\" ult_hide_row_large_screen=\"\" ult_hide_row_desktop=\"\" ult_hide_row_tablet=\"\" ult_hide_row_tablet_small=\"\" ult_hide_row_mobile=\"\" ult_hide_row_mobile_large=\"\" us_notification=\"\" el_class=\"vawes-svg\" bg_grad=\"\" bg_color_value=\"\" controls_color=\"\" notification=\"\"][vc_column el_class=\"align_center\"][vc_raw_html][/vc_raw_html][/vc_column][/vc_row][vc_row height=\"auto\" width=\"full\" us_bg_overlay_color=\"#f8faff\" el_id=\"projects\"][vc_column][us_separator size=\"large\"][vc_raw_html][/vc_raw_html][vc_column_text css_animation=\"bottom-to-top\" el_class=\"tariffs-for-mob-int\"]\r\n<h2><strong>Тарифы на мобильный интернет в Европе\r\n<span class=\"highlight highlight_primary\">для туристов</span></strong></h2>\r\n<p style=\"text-align: center;\">Безлимитный интернет и дешевые звонки за границей без абонентской платы</p>\r\n[/vc_column_text][us_separator size=\"large\"][vc_row_inner el_class=\"all-countries\"][vc_column_inner][vc_column_text]\r\n<h3 style=\"margin-bottom: 20px; text-align: center;\"><b>Список стран</b></h3>\r\n[/vc_column_text][vc_raw_html][/vc_raw_html][/vc_column_inner][vc_column_inner][/vc_column_inner][/vc_row_inner][us_separator size=\"large\"][vc_column_text el_class=\"pricing-columns\"]\r\n<div class=\"pricing pricing-palden\">\r\n<div class=\"pricing-item\">\r\n<div class=\"pricing-plan-title\">\r\n<h3 class=\"pricing-plan-title_pack\">ALTA MUNDO 1 GB</h3>\r\n<p class=\"pricing-plan-title_pack_description\">Вы хотите провести в дороге больше недели и постоянно оставаться на связи</p>\r\n<p class=\"pricing-plan-title_pack_countries\">страны Евросоюза</p>\r\n\r\n</div>\r\n<ul class=\"pricing-feature-list\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"pricing-feature-list\">\r\n 	<li class=\"pricing-feature row-item-1\">\r\n<p class=\"pricing-internet-traffic-title\">Объем интернета</p>\r\n<p class=\"pricing-internet-traffic-description\">2 ГБ\r\n<span class=\"by-action\">по акции*</span></p>\r\n<p class=\"pricing-internet-expiration-date-title\">Срок действия</p>\r\n<p class=\"pricing-internet-expiration-date-description\">30 дней</p>\r\n<p class=\"pricing-internet-price-title\">Стоимость</p>\r\n<p class=\"pricing-internet-price-description\">7€</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div class=\"pricing-buttons-block\">\r\n<div class=\"w-btn-wrapper align_center\"><a class=\"w-btn style_solid color_midnight icon_none\" style=\"font-size: 16px; background: #fff; border-color: #f8faff!important;\" href=\"#prices\"><span class=\"w-btn-label\" style=\"color: #2c3e50;\">Купить</span></a></div>\r\n</div>\r\n</div>\r\n<div class=\"pricing-item\">\r\n<div class=\"pricing-plan-title\">\r\n<h3 class=\"pricing-plan-title_pack\">ALTA MUNDO 2 GB</h3>\r\n<p class=\"pricing-plan-title_pack_description\">Вы хотите провести в дороге больше недели и постоянно оставаться на связи</p>\r\n<p class=\"pricing-plan-title_pack_countries\">страны Евросоюза</p>\r\n\r\n</div>\r\n<ul class=\"pricing-feature-list\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"pricing-feature-list\">\r\n 	<li class=\"pricing-feature row-item-1\">\r\n<p class=\"pricing-internet-traffic-title\">Объем интернета</p>\r\n<p class=\"pricing-internet-traffic-description\">4 ГБ\r\n<span class=\"by-action\">по акции*</span></p>\r\n<p class=\"pricing-internet-expiration-date-title\">Срок действия</p>\r\n<p class=\"pricing-internet-expiration-date-description\">30 дней</p>\r\n<p class=\"pricing-internet-price-title\">Стоимость</p>\r\n<p class=\"pricing-internet-price-description\">10€</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div class=\"pricing-buttons-block\">\r\n<div class=\"w-btn-wrapper align_center\"><a class=\"w-btn style_solid color_midnight icon_none\" style=\"font-size: 16px; background: #fff; border-color: #f8faff!important;\" href=\"#prices\"><span class=\"w-btn-label\" style=\"color: #2c3e50;\">Купить</span></a></div>\r\n</div>\r\n</div>\r\n<div class=\"pricing-item\">\r\n<div class=\"pricing-plan-title\">\r\n<h3 class=\"pricing-plan-title_pack\">ALTA MUNDO 3 GB</h3>\r\n<p class=\"pricing-plan-title_pack_description\">Вы хотите отправиться в долгое путешествие или совершить несколько поездок в течение месяца</p>\r\n<p class=\"pricing-plan-title_pack_countries\">страны Евросоюза</p>\r\n\r\n</div>\r\n<ul class=\"pricing-feature-list\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"pricing-feature-list\">\r\n 	<li class=\"pricing-feature row-item-1\">\r\n<p class=\"pricing-internet-traffic-title\">Объем интернета</p>\r\n<p class=\"pricing-internet-traffic-description\">6 ГБ\r\n<span class=\"by-action\">по акции*</span></p>\r\n<p class=\"pricing-internet-expiration-date-title\">Срок действия</p>\r\n<p class=\"pricing-internet-expiration-date-description\">30 дней</p>\r\n<p class=\"pricing-internet-price-title\">Стоимость</p>\r\n<p class=\"pricing-internet-price-description\">15€</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div class=\"pricing-buttons-block\">\r\n<div class=\"w-btn-wrapper align_center\"><a class=\"w-btn style_solid color_midnight icon_none\" style=\"font-size: 16px; background: #fff; border-color: #f8faff!important;\" href=\"#prices\"><span class=\"w-btn-label\" style=\"color: #2c3e50;\">Купить</span></a></div>\r\n</div>\r\n</div>\r\n<div class=\"pricing-item\">\r\n<div class=\"pricing-plan-title\">\r\n<h3 class=\"pricing-plan-title_pack\">Опция Everywhere</h3>\r\n<p class=\"pricing-plan-title_pack_description\">Вы хотите пользоваться интернетом только тогда, когда он действительно нужен</p>\r\n<p class=\"pricing-plan-title_pack_countries\"><a data-fancybox=\"\" data-src=\"#hidden-content-countries\"><span class=\"my-highlight-countries\">Показать страны</span></a></p>\r\n\r\n</div>\r\n<ul class=\"pricing-feature-list\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"pricing-feature-list\">\r\n 	<li class=\"pricing-feature row-item-1\">\r\n<p class=\"pricing-internet-traffic-title\">Объем интернета</p>\r\n<p class=\"pricing-internet-traffic-description\">По 100 МБ</p>\r\n<p class=\"pricing-internet-expiration-date-title\">Срок действия</p>\r\n<p class=\"pricing-internet-expiration-date-description\">24 часа</p>\r\n<p class=\"pricing-internet-price-title\">Стоимость</p>\r\n<p class=\"pricing-internet-price-description\">7€ за 100 МБ</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div class=\"pricing-buttons-block\">\r\n<div class=\"w-btn-wrapper align_center\"><a class=\"w-btn style_solid color_midnight icon_none\" style=\"font-size: 16px; background: #fff; border-color: #f8faff!important;\" href=\"#prices\"><span class=\"w-btn-label\" style=\"color: #2c3e50;\">Купить</span></a></div>\r\n</div>\r\n</div>\r\n<blockquote class=\"blockquote-atten\">* Акция действует до 31.05.2018\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Используйте только в тех странах, где действует подключенный интернет-пакет.\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Во всех других странах дешевые тарифы не действуют!!! В странах, не входящих в зону действия, всегда отключайте интернет в устройстве, т.к. в них действует только по Мб тарификация, за 1 Мб 12 евро. Если интернет не отключен, то все денежные средства сразу списываются и баланс сим-карты становиться отрицательным.\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> При минусовом и нулевом балансе карта не видит мобильную сеть!\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Оператор не принимает претензии от Абонентов за обоснованные списания, если карта подключалась к интернету в странах не входящих в зону действия, поэтому при использовании в ЕС, но на границе или в приграничной зоне со странами не из списка, выбирайте оператора вручную.\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> При перемещение внутри зоны действия из одной страны в другую, пакеты продолжают действовать (повторного подключения не требуется)\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Испания – является домашней зоной.</blockquote>\r\n</div>\r\n[/vc_column_text][/vc_column][/vc_row][vc_row height=\"large\" us_bg_overlay_color=\"#f8faff\" el_id=\"services\"][vc_column][us_single_image image=\"1243\" size=\"full\" align=\"center\"][vc_raw_html][/vc_raw_html][us_separator size=\"large\"][vc_row_inner columns_type=\"boxes\" gap=\"20\" el_class=\"sim-card-features\"][vc_column_inner width=\"1/3\"][vc_column_text]\r\n<div class=\"w-iconbox-icon\" style=\"font-size: 28px;\"><i class=\"material-icons\">wifi_tethering</i></div>\r\n<h3 class=\"w-iconbox-title\" style=\"text-align: center;\"><strong>Раздача интернета</strong></h3>\r\nТарифы разрешают использовать смартфоны или роутеры под раздачу интернета (шаринг) на несколько пользователей (устройств).[/vc_column_text][/vc_column_inner][vc_column_inner width=\"1/3\"][vc_column_text]\r\n<div class=\"w-iconbox-icon\" style=\"font-size: 28px;\"><i class=\"material-icons\">done</i></div>\r\n<h3 class=\"w-iconbox-title\" style=\"text-align: center;\"><strong>Безлимитный доступ в интернет</strong></h3>\r\nИнтернет не будет отключен, если Абонент использовал весь трафик из пакета, то происходит снижение скорости до 64 Кб/с[/vc_column_text][/vc_column_inner][vc_column_inner width=\"1/3\"][vc_column_text]\r\n<div class=\"w-iconbox-icon\" style=\"font-size: 28px;\"><i class=\"material-icons\">autorenew</i></div>\r\n<h3 class=\"w-iconbox-title\" style=\"text-align: center;\"><strong>Переподключение пакетов</strong></h3>\r\nПакеты разрешено пере подключать, если трафик исчерпан.\r\n\r\n[/vc_column_text][/vc_column_inner][/vc_row_inner][vc_column_text css=\".vc_custom_1518786938694{margin-top: 30px !important;}\"]\r\n<div class=\"number-format\">\r\n\r\n<span style=\"font-weight: 400;\">Формат номера: </span><b>+34</b><span style=\"font-weight: 400;\"> ХХХХХХХХХ</span>\r\n\r\n</div>\r\n[/vc_column_text][vc_row_inner disable_element=\"yes\"][vc_column_inner animate=\"afb\" width=\"1/3\"][us_iconbox icon=\"material|wifi_tethering\" style=\"outlined\" iconpos=\"left\" size=\"28px\" title=\"<strong>Раздача интернета</strong>\" title_tag=\"h3\" external=\"\"]Тарифы разрешают использовать смартфоны или роутеры под раздачу интернета (шаринг) на несколько пользователей (устройств).[/us_iconbox][/vc_column_inner][vc_column_inner animate=\"afb\" width=\"1/3\"][us_iconbox icon=\"material|done\" style=\"outlined\" iconpos=\"left\" size=\"28px\" title=\"<strong>Постоянный доступ в интернет</strong>\" title_tag=\"h3\" external=\"\"]Интернет не будет отключен, если Абонент использовал весь трафик из пакета, то происходит снижение скорости до 64 Кб/с[/us_iconbox][/vc_column_inner][vc_column_inner animate=\"afb\" width=\"1/3\"][us_iconbox icon=\"material|autorenew\" style=\"outlined\" iconpos=\"left\" size=\"28px\" title=\"<strong>Переподключение пакетов</strong>\" title_tag=\"h3\" external=\"\"]Пакеты разрешено пере подключать, если трафик исчерпан.[/us_iconbox][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row height=\"large\" us_bg_overlay_color=\"#ffffff\" el_id=\"prices\"][vc_column][vc_column_text]\r\n<h2><strong>Купить симку Orange для Европы\r\n<span class=\"highlight highlight_primary\">за 690 руб.</span></strong></h2>\r\n[/vc_column_text][us_separator type=\"default\" size=\"small\" color=\"primary\" text=\"Самый дешевый интернет в роуминге за границей\" title_tag=\"p\"][us_separator size=\"large\"][vc_row_inner el_class=\"price-calc\"][vc_column_inner][vc_raw_html][/vc_raw_html][product_page id=\"1346\"][/vc_column_inner][/vc_row_inner][us_separator size=\"small\"][vc_column_text el_id=\"payment-and-delivery\"]\r\n<h3>При оформлении сим-карты Orange:</h3>\r\n<ul>\r\n 	<li style=\"list-style-type: none;\">\r\n<ul>\r\n 	<li>Выберете необходимую дату регистрации, чтобы пакет со сроком – 30 дней использовать в оптимальные для Вас сроки.\r\n(рекомендуем указывать дату активации за день до поездки с учетом выходных и праздничных дней)</li>\r\n 	<li>В заявленную дату регистрации сим-карты, на Ваш e–mail будет отправлено уведомление, что карта активирована Оператором на ваше ФИО, с выбранным Вами пакетом Мундо.</li>\r\n 	<li>Используйте сим-карту только в странах ЕС, при первом включении активация в сети может осуществляться до 5 минут.</li>\r\n 	<li>На сим-карту Оранж придет СМС сообщение, что Вы находитесь в такой то стране, у Вас подключен (такой то) тариф Мундо. Если действует акция по удвоению пакета, то об этом будет сообщено в отдельной СМС.</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<h3>Оплата и доставка</h3>\r\n<ul>\r\n 	<li style=\"list-style-type: none;\">\r\n<ul>\r\n 	<li>Доступные методы доставки:\r\n<b>пункт выдачи</b> – бесплатно. Заказ необходимо оплатить при оформлении в интернет-магазине.\r\n<b>доставка почтой РФ</b> – бесплатно. Оплата заказа возможна на сайте при оформлении или после получения (после получения на Вашу почту будет отпралена ссылка на оплату).\r\n<b>курьерская доставка</b> – по Москве 300 руб. | в другие города РФ от 300 руб. Оплата заказа возможна на сайте при оформлении или наличными курьеру при получении</li>\r\n 	<li>Доступные способы оплаты:\r\n<b>Банковская карта</b> – Visa, MasterCard, Мир.\r\n<b>Кошелек Яндекс.Деньги</b> – оплата из кошелька в Яндекс.Деньгах.\r\n<b>Наличными через кассы и терминалы</b> – оплата в салонах связи или через кассы партнеров.\r\n<b>Кошелек WebMoney </b>– оплата из кошелька в системе WebMoney.\r\n<b>Альфа-Клик </b>– оплата через Альфа-Клик.\r\n<b>MasterPass </b>– оплата через MasterPass.</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n[/vc_column_text][/vc_column][/vc_row][vc_row height=\"large\" us_bg_overlay_color=\"#f8faff\" el_id=\"full-description\"][vc_column][us_single_image image=\"1242\" size=\"full\" align=\"center\"][vc_column_text]\r\n<h2><strong>Сим-карта ORANGE\r\n<span class=\"highlight highlight_primary\">для Европы</span></strong></h2>\r\n[/vc_column_text][us_separator type=\"default\" size=\"small\" color=\"custom\" bdcolor=\"#3e396b\" text=\"Самый дешевый мобильный интернет в Европе и выгодные звонки\" title_tag=\"p\"][us_separator size=\"large\"][vc_column_text el_class=\"full-description-content\" el_id=\"full-description-content\"]\r\n<ul style=\"list-style-type: none;\">\r\n 	<li><a class=\"smooth-scroll\" href=\"#list-of-countries\">1. Список стран</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#Internet-packages-mundo\">2. Интернет-пакеты Mundo от Orange</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#calls-and-sms-in-eu\">3. Звонки и СМС в странах ЕС</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#calls-in-spain\">4. Звонки в Испании и в Россию</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#internet-setting\">5. Настройка интернета</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#checking-the-balance-orange\">6. Проверка баланса Orange</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#personal-area\">7. Личный кабинет</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#additional-package-connection\">8. Подключение дополнительных пакетов для интернета в Европе</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#disabling-packages\">9. Отключение пакетов и переход на тариф Llama</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#tariff-without-monthly-fee\">10. Тариф без абонентской платы – Llama</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#Internet-in-future-travels\">11. Как подключать интернет в последующих путешествиях в Европу</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#everywhere-option\">12. Опция Everywhere</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#sim-card-expiration\">13. Сроки действия сим карт Orange</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#other-conditions\">14. Внимание! Прочие условия:</a>\r\n<ul style=\"margin-bottom: 10px; margin-top: 0;\">\r\n 	<li><a class=\"smooth-scroll\" href=\"#activation-sim-card-orange\">Активация сим карты Orange</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#iphone-ipad\">Прежде чем использовать симку в устройствах iPhone или iPad</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#where-can-i-use-mundo\">Где можно использовать тариф Мундо</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#action-4g-on-the-sim-card\">Действие 4G на сим карте Orange</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#additional-information-about-sim-cards\">Дополнительная информация о сим-картах</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#trouble-shooting\">Ограничение Оператора связи</a></li>\r\n</ul>\r\n</li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#troubleshooting\">15. Устранение неисправностей сим-карты</a></li>\r\n 	<li><a class=\"smooth-scroll\" href=\"#faq\">16. Вопросы и ответы: Orange Mundo</a></li>\r\n</ul>\r\n[/vc_column_text][vc_row_inner][vc_column_inner][vc_column_text]\r\n<h3 id=\"tariff-comparison\"><b>Сравнение тарифов</b></h3>\r\n[/vc_column_text][vc_raw_html css=\".vc_custom_1519917824547{margin-bottom: 95px !important;}\"][/vc_raw_html][/vc_column_inner][/vc_row_inner][vc_column_text]\r\n<h3 id=\"list-of-countries\" style=\"text-align: center; margin-top: 37px;\"><b>Список стран для пакетов Mundo</b></h3>\r\n[/vc_column_text][vc_raw_html][/vc_raw_html][vc_column_text]\r\n<h3 id=\"Internet-packages-mundo\" style=\"text-align: center; margin-top: 37px;\"><b>Интернет-пакеты Mundo от Orange</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nПакет 1: <b class=\"my-highlight\">ALTA MUNDO 1GB</b> — включено <b class=\"my-highlight\">1ГБ</b> за <b class=\"my-highlight\">€  7</b> на 30 дней\r\n\r\nПакет 2: <b class=\"my-highlight\">ALTA MUNDO 2GB</b> — включено <b class=\"my-highlight\">2ГБ</b> за <b class=\"my-highlight\">€10</b> на 30 дней\r\n\r\nПакет 3: <b class=\"my-highlight\">ALTA MUNDO 3GB</b> — включено <b class=\"my-highlight\">3ГБ</b> за <b class=\"my-highlight\">€15</b> на 30 дней\r\n<p style=\"margin-top: 37px; font-weight: 600;\">Рекомендуем прочитать раздел <a class=\"smooth-scroll\" href=\"#other-conditions\"><span class=\"my-highlight\">Внимание</span></a></p>\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"calls-and-sms-in-eu\"><b>Звонки и СМС в странах ЕС</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nВходящие звонки — <span class=\"my-highlight\">бесплатно с любых номеров</span>\r\n\r\nИсходящие звонки (только на европейские номера) — <span class=\"my-highlight\">0,08€/мин</span>\r\n\r\nСтоимость соединения — <span class=\"my-highlight\">0,30€</span>\r\n\r\nSms на номера ЕС — <span class=\"my-highlight\">0,73€</span>\r\n\r\nSms на российские номера — <span class=\"my-highlight\">1,21€</span>\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"calls-in-spain\"><b>Звонки в Испании и в Россию</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\n<b>Безлимитные входящие/исходящие вызовы внутри Испании (в течение 7 дней после последнего пополнения баланса):</b>\r\n<ul>\r\n 	<li>по истечении 7 дней стоимость исходящего вызова составит <span class=\"my-highlight\">€0,01 за минуту</span></li>\r\n 	<li>плата за соединение — <span class=\"my-highlight\">€0,30</span></li>\r\n</ul>\r\n<b>Звонки из Испании в Россию:</b>\r\n<ul>\r\n 	<li style=\"margin-bottom: 25px;\">на стационарные номера — <span class=\"my-highlight\">€0,07/мин</span></li>\r\n 	<li style=\"margin-bottom: 25px;\">на мобильные номера — <span class=\"my-highlight\">€0,29/мин</span></li>\r\n 	<li>плата за соединение — <span class=\"my-highlight\">€0,30</span></li>\r\n</ul>\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"internet-setting\"><b>Настройка интернета</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nНастройка, при включение смартфона, роутера или планшета, производиться автоматически, но рекомендуем проверять правильность <span style=\"font-weight: 600;\">«Точки доступа» (APN)</span>:\r\n\r\nAPN <b class=\"my-highlight\">orangeworld</b> или <b class=\"my-highlight\">internet</b>\r\n\r\nЛогин и Пароль: <b class=\"my-highlight\">orange</b>\r\n<p style=\"margin-bottom: 12px;\"><span style=\"font-weight: bold;\">Перед использованием на своем устройстве, проверьте активны ли:</span></p>\r\n\r\n<div class=\"internet-sett\">\r\n\r\n<span class=\"my-highlight\">Сотовые данные</span>\r\n<p style=\"text-align: center;\">и</p>\r\n<span class=\"my-highlight\">Роуминг данных</span>\r\n\r\n</div>\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"checking-the-balance-orange\"><strong>Проверка баланса Orange</strong></h3>\r\n<div class=\"full-description-block\">\r\n\r\nПроверка баланса сим-карты — бесплатный звонок на номер: <span class=\"my-highlight\">*111# <i class=\"material-icons\" style=\"vertical-align: bottom; padding: 6px; background: #f7f7f7; border-radius: 100px; color: #08b85c; font-weight: 600;\">phone</i></span>\r\n\r\nБесплатная проверка баланса только <span class=\"my-highlight\">5 раз в месяц</span>\r\n\r\nКаждая последующая проверка USSD-командой будет стоить <span class=\"my-highlight\">€0,25</span>\r\n\r\n<span style=\"font-weight: 600;\">Проверяйте баланс через «Личный кабинет»</span>\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"personal-area\"><b>Личный кабинет</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nПланируете сим-карту использовать многократно?\r\n\r\nОбязательно рекомендуем зарегистрировать <span style=\"font-weight: 600;\">«Личный кабинет»</span> на сайте оператора <a href=\"https://orange.es/\" target=\"_blank\" rel=\"noopener\"><span class=\"my-highlight\">orange.es</span></a>\r\n<blockquote>Если<i> личный кабинет зарегистрирован, то Вы сможете самостоятельно подключать пакеты, менять тарифные планы, смотреть остаток трафика и баланс.</i></blockquote>\r\nРегистрация кабинета:<a data-fancybox=\"\" data-src=\"#hidden-content\"><span class=\"my-highlight\">Инструкция</span></a>\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"additional-package-connection\"><b>Подключение дополнительных пакетов для интернета в Европе</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\n<strong>Вы не рассчитали необходимый объем пакета и исчерпали весь трафик?</strong>\r\n\r\nИнтернет не отключается — у Вас включится ограничение скорости до 64 кб/с.\r\n\r\nПакеты Mundo можно переподключить через <strong>«Личный кабинет»</strong> на сайте <a href=\"https://orange.es/\" target=\"_blank\" rel=\"nofollow noopener\"><span class=\"my-highlight\">orange.es</span></a>\r\n\r\nТак же попробовать подключить с помощью платных СМС:\r\n<table class=\"instr-table\">\r\n<tbody>\r\n<tr>\r\n<td>Интернет-пакет</td>\r\n<td>СМС сообщение на номер 1470</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 1 ГБ за € 7</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">MAS GIGAS MUNDO 1GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 2 ГБ за €10</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">MAS GIGAS MUNDO 2GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 3 ГБ за €15</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">MAS GIGAS MUNDO 3GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\nПри подключении пакета, приходит подтверждающая СМС: <span class=\"my-highlight\">ALTA MUNDO <span style=\"font-weight: 400;\">Х</span> GB</span>\r\n\r\nПри переподключении пакета <b>Mundo,</b>  остаток трафика из предыдущего пакета сгорает.\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"disabling-packages\"><b>Отключение пакетов и переход на тариф Llama</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nМундо 1 Гб, 2 Гб и 3 Гб автоматически пролонгируются через 30 дней после активации, выбранного пакета, т.е. с баланса, при наличие необходимой суммы, спишется сумма, равная стоимости подключенного пакета.\r\n\r\n<strong>1.</strong> Если сумма на балансе карты <span class=\"my-highlight\" style=\"color: #fd831d;\">меньше</span> стоимости подключенного пакета,\r\n<ul style=\"list-style: none; margin-left: 0;\">\r\n 	<li style=\"margin-bottom: 27px;\"><strong class=\"my-highlight\">ALTA MUNDO 1GB</strong> за <strong class=\"my-highlight\">€  7</strong>, остаток баланса <strong class=\"min-symb\">&lt;</strong>   <strong class=\"my-highlight\">7 евро</strong></li>\r\n 	<li style=\"margin-bottom: 27px;\"><strong class=\"my-highlight\">ALTA MUNDO 2GB</strong> за <strong class=\"my-highlight\">€10</strong>, остаток баланса <strong class=\"min-symb\">&lt;</strong> <strong class=\"my-highlight\">10 евро</strong></li>\r\n 	<li><strong class=\"my-highlight\">ALTA MUNDO 3GB</strong> за <strong class=\"my-highlight\">€15</strong>, остаток баланса <strong class=\"min-symb\">&lt;</strong> <strong class=\"my-highlight\">15 евро</strong></li>\r\n</ul>\r\nто оставляйте карту до следующей поездке с этим тарифом.\r\n\r\n<strong>2.</strong> Если сумма на балансе карты больше стоимости подключенного пакета, то чтобы не списалась сумма на пролонгацию, смените Ваш тарифный план на Llama с абонентской платой – 0.\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"tariff-without-monthly-fee\"><b>Тариф без абонентской платы – Llama:</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\n<strong>Отключить Mundo пакеты</strong> — написать <strong>СМС</strong>-сообщение на номер <strong>1470 </strong>с текстом: <strong>ALTA TARIFA LLAMA</strong>\r\n\r\nСтоимость в разных странах сервисных СМС может составить <b>до €1,21</b>\r\n\r\nЕсли на номер Оранж не поступило СМС о подключении тарифа Llama, то сделайте это через <strong>«Личный кабинет»</strong> на сайте <a href=\"https://orange.es/\" target=\"_blank\" rel=\"nofollow noopener\"><span class=\"my-highlight\">orange.es</span></a>\r\n\r\nИзменять тариф на сим-карте Orange бесплатно можно всего 5 раз, последующие изменения стоит €6. За SMS на номер 1470 оператором может взиматься плата.\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"Internet-in-future-travels\"><b>Как подключать интернет в последующих путешествиях в Европу</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nПодключайте пакеты бесплатно через личный кабинет на сайте <a href=\"https://orange.es/\" target=\"_blank\" rel=\"nofollow noopener\"><span class=\"my-highlight\">orange.es</span></a>\r\n\r\nРегистрация кабинета: <a href=\"https://euroroaming.ru/instruction-orange-go-europe/\"><span class=\"my-highlight\" data-fancybox=\"\" data-src=\"#hidden-content\">Инструкция</span></a>\r\n\r\nПодключение пакетов с помощью смс на территории Европы не всегда доступно и может взиматься плата за смс\r\n<table class=\"instr-table\">\r\n<tbody>\r\n<tr>\r\n<td>Интернет-пакет</td>\r\n<td>СМС сообщение на номер 1470</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 1 ГБ за € 7</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">ALTA MUNDO 1GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 2 ГБ за €10</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">ALTA MUNDO 2GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Активация пакета 3 ГБ за €15</td>\r\n<td>\r\n<p class=\"alta-mundo\">SMS: <span class=\"alta-mundo-txt\">ALTA MUNDO 3GB</span> на номер <span class=\"alta-mundo-tel\">1470</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\nПри подключении пакета, приходит подтверждающая СМС: <span class=\"my-highlight\">ALTA MUNDO <span style=\"font-weight: 400;\">Х</span> GB</span>\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"everywhere-option\"><b>Опция Everywhere</b></h3>\r\n<div class=\"full-description-block\">\r\n\r\nОпция <strong>Everywhere</strong> дает возможность для использовании сим-карты Orange в Швейцарии, США, Канаде, Мексике, Доминиканской Республике, Чили, Марокко, Японии и Китае (кроме Гонконга и Макао *).\r\n\r\nПеред поездкой обязательно проверяйте наличие вашей страны на сайте оператора Orange в разделе <a style=\"font-weight: bold;\" href=\"https://ayuda.orange.es/particulares/movil/roaming-internacional/2314-si-vas-a-viajar-fuera-de-la-union-europea-no-olvides-activar-everywhere\" target=\"_blank\" rel=\"nofollow noopener\">Zona principal</a>, т.к. список стран может изменяться оператором Orange.\r\n\r\nОпция позволяет использовать пакеты:\r\n<ul>\r\n 	<li style=\"margin-bottom: 30px;\">Интернет — <span class=\"my-highlight\">7 евро</span> за каждые <span class=\"my-highlight\">100 МБ</span> трафика.</li>\r\n 	<li>Звонки — <span class=\"my-highlight\">7 евро</span> за каждые <span class=\"my-highlight\">30 минут</span> на местные номера.</li>\r\n</ul>\r\nАктивация пакетов происходит по фактическому выходу в сеть или совершению/приему любого звонка на территории списка стран.\r\n\r\n<strong style=\"color: #ff8f00;\">Активация опции Everywhere</strong>: отправьте <b>SMS</b> с текстом <b style=\"color: #ff8f00;\">EW </b>на <b style=\"color: #009688;\">22095</b>\r\n\r\n<b></b><b style=\"color: #f23f00;\">Деактивация опции</b>: отправьте <b>SMS</b> с текстом <b style=\"color: #f23f00; white-space: nowrap;\">BAJA EW</b> на номер <b style=\"color: #009688;\">22095</b>\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"sim-card-expiration\"><strong>Сроки действия сим-карт Orange</strong></h3>\r\n<div class=\"full-description-block\">\r\n\r\nПри обязательном и своевременном пополнении баланса сим карты, минимум на 5€ ваша сим-карта станет бессрочной.\r\n\r\nНо если остаток баланса менее чем 2 Евро то, через 5 месяцев, после последнего пополнения, карта рециркулируется навсегда, а баланс списывается оператором и восстановлению не подлежит.\r\n\r\nНо если баланс больше, чем 2 Евро то срок сим-карты увеличивается до 11 месяцев с даты последнего пополнения баланса, если в этот срок баланс не пополняется, то сим-карта удаляется в системе Оператора и после этого не восстанавливается, остаток на балансе сгорает.\r\n\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"other-conditions\"><strong>Внимание! Прочие условия</strong></h3>\r\n<div class=\"full-description-block\">\r\n<h4 id=\"activation-sim-card-orange\"><b>Активация сим карты Orange: </b></h4>\r\nРегистрация карты Оператором на ваше имя может занимать от 10 минут до 24 часов.\r\n\r\nПри регистрации происходит активация, выбранного пакета Мундо, который действует 30 дней, а на e-mail, указанный при покупке, отправляется об этом сообщение.\r\n\r\nПри первоначальном подключении сим карты к мобильной сети, на смартфон поступит подтверждающее SMS: ALTA MUNDO Х GB, т.е. уведомление о тарифе, который уже подключен на вашей сим карте.\r\n<p id=\"iphone-ipad\">Прежде чем <b>использовать симку в устройствах iPhone или iPad</b>, обязательно прочитайте <b>\"Смотрите\"</b></p>\r\n\r\n<h4 id=\"where-can-i-use-mundo\"><b>Где можно использовать тариф Мундо: </b></h4>\r\n<ul>\r\n 	<li>Только в тех странах, где действует подключенный интернет-пакет.</li>\r\n 	<li>Во всех других странах дешевые тарифы не действуют!!! В странах, не входящих в зону действия, всегда отключайте интернетв устройстве, т.к. в них действует только по Мб тарификация, за 1 Мб 12 евро. Если интернет не отключен, то все денежные средства сразу списываются и баланс сим-карты становиться отрицательным.</li>\r\n 	<li>При минусовом и нулевом балансе карта не видит мобильную сеть!</li>\r\n 	<li>Orange спишет с баланса с устройства с не отключенным интернетом в Андорре, в Швейцарии, в Монако, в Турции и на севере Кипра (территория Турции), в России, в Крыму, в Белоруссии, в Украине, в Черногории, в Сербии и др. странах, не входящих в ЕС, а также на морских и на воздушных судах.</li>\r\n 	<li>В приграничных местах, не далеко от тех стран, которые не входят в действия тарифа, выбирайте оператора заранее вручную. Рядом со Швейцарией можно автоматически подключиться к оператору этой страны, а на границе в Польше к Белорусскому.</li>\r\n 	<li>В Греции на о. Кос, на о. Родос и др., установленное в телефонах по умолчанию, автоматическое определение сети оператора теоретически может подключить к Турецким операторам мобильной связи. Поэтому находясь там, самостоятельно определите Греческого оператора и поставьте в настройках телефона (Выбрать вручную).</li>\r\n</ul>\r\n<blockquote><i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Оператор не принимает претензии от Абонентов за обоснованные списания, если карта подключалась к интернету в странах не входящих в зону действия, поэтому при использовании в ЕС, но на границе или в приграничной зоне со странами не из списка, выбирайте оператора вручную.\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> При перемещение внутри зоны действия из одной страны в другую, пакеты продолжают действовать (повторного подключения не требуется)\r\n\r\n<i class=\"material-icons\" style=\"vertical-align: middle;\">info_outline</i> Испания – является домашней зоной.</blockquote>\r\n<h4 id=\"action-4g-on-the-sim-card\"><b>Действие 4G на сим карте Orange:</b></h4>\r\nОператор не может дать гарантии на постоянную работу в сетях 4G в тех странах, где трафик осуществляется через сети партнеров.\r\n<h4 id=\"additional-information-about-sim-cards\"><b>Дополнительная информация о сим-картах:</b></h4>\r\n<ul>\r\n 	<li>По окончании трафика включается ограничение скорости до 64 кб/с. Проверить Остаток интернет-трафика, отправить SMS «CONSUMO MB MUNDO» на номер 1470 или (+34656101470). Стоимость таких SMS составляет до €1,21.</li>\r\n 	<li>Расходы трафика лучше проверять в «Личном кабинете». Пакеты, при необходимости дополнительного трафика на 4G/3G скорости можно пере подключить.</li>\r\n 	<li>Для подключения нового пакета в «Личном кабинете» Вам необходимо:\r\n<ol>\r\n 	<li>Удостовериться в том,что на балансе сим карты хватает средств для активации дополнительного пакета.</li>\r\n 	<li>Выбрать необходимый пакет в подразделе cambiar tarifa.</li>\r\n 	<li>Получить SMS-подтверждение, что пакет подключен.</li>\r\n 	<li>И перезагрузить Ваше мобильное устройство.</li>\r\n</ol>\r\n</li>\r\n 	<li>Оператор не всегда гарантирует, что активации пакетов будет своевременной и это может привести к необоснованным списаниям, поэтому получите подтверждающее СМС: ALTA MUNDO Х GB и желательно выдержать временной интервал перед очередным использованием устройства в сети.</li>\r\n</ul>\r\n<h4 id=\"trouble-shooting\"><b>Ограничение Оператора связи:</b></h4>\r\nВсе мобильные операторы всегда оставляют за собой право изменять скорость доступа в сети Интернет, этим ограничивая использование трафика.\r\n\r\nЭто происходит в следующих ситуациях:\r\n<ul>\r\n 	<li>При излишнем скачивании объемных файлов, что вызывает ухудшение качества связи для других абонентов, т.к. вызывает недопустимую нагрузку на сеть;</li>\r\n 	<li>При нелегальном или мошенническом использование интернета;</li>\r\n 	<li>Запрещается скачивание через мобильный интернет торрент-файлов.</li>\r\n</ul>\r\n[us_separator type=\"invisible\" size=\"small\"][us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"]\r\n\r\n</div>\r\n<h3 id=\"troubleshooting\"><strong>Устранение неисправностей сим-карты</strong></h3>\r\n<div class=\"full-description-block\">\r\n<div class=\"my-accordion\">\r\n<h4 id=\"simcard-does-not-see-network\"><b>Сим-карта не видит сеть оператора:</b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>При первичной активации новой сим-карты поиск сети может занять продолжительное время до 10 мин</li>\r\n 	<li>Необходимо подключить и отключить «Режим полета»</li>\r\n 	<li>Перейти с автоматической настройки сети на выбрать Оператора вручную</li>\r\n 	<li>Перегрузите телефон</li>\r\n 	<li>Необходимо извлечь сим-карту и еще раз установить в телефон</li>\r\n 	<li>Необходимо проверить баланс, при нуле у симкарты нет доступа в сеть</li>\r\n 	<li>Вставить сим-карту в другой телефон</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n</ul>\r\n</div>\r\n<div class=\"my-accordion\">\r\n<h4 id=\"smartphone-does-not-see-simcard\"><b>Смартфон не видит сим-карту orange: </b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>Отключить «Режим полета»</li>\r\n 	<li>Перегрузить телефон</li>\r\n 	<li>Извлечь и опять установить сим-карту вновь</li>\r\n 	<li>Вставить сим-карту в иное устройство</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n</ul>\r\n</div>\r\n<div class=\"my-accordion\">\r\n<h4 id=\"network-is-defined\"><b>Сеть определяется, мобильный интернет не работает:</b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>Проверить отключен ли «Режим полета»</li>\r\n 	<li>Перегрузить смартфон</li>\r\n 	<li>Отключите и подключите «Сотовые данные или Мобильный интернет»</li>\r\n 	<li>Отключите и подключите «Роуминг данных или Роуминг-интернет»</li>\r\n 	<li>В проверьте правильные ли настройки точки доступа - APN</li>\r\n 	<li>Проверить баланс</li>\r\n 	<li>Проверить подключен ли интернет-пакет и не закончился ли на нем трафик</li>\r\n 	<li>В iPhone необходимо проверить подключена ли программа выхода в интернет, например Safari.</li>\r\n 	<li>Подключите раздачу, в режим модема, если другие пользователи могут через Вас выходить в сеть и пользоваться интернетом, то значит на вашем телефоне отключена программа выхода в интернет, например Safari.</li>\r\n 	<li>Попробуйте сделать исходящий звонок (гудок), чтобы удостовериться, что проблема только в интернете</li>\r\n 	<li>Проверить не превысили ли Вы максимальный (установленный по умолчанию оператором) лимит - €50 в месяц в роуминге, чтобы снять данное ограничение надо позвонить на номер*118# или отправить SMS с текстом:«NAVEGAR» на номер 22095</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n</ul>\r\n</div>\r\n<div class=\"my-accordion\">\r\n<h4 id=\"low-speed\"><b>Низкая скорость интернета:</b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>Перегрузите ваше устройство</li>\r\n 	<li>Попробуйте перейти с автоматического определения сети и выбрать Оператора вручную</li>\r\n 	<li>Проверьте через личный кабинет не закончился ли трафик пакета, скорость снижается до 64 Кб (должны приходить предупреждающие СМС и предложение продлить пакет)</li>\r\n 	<li>Попробуйте в другом месте, возможно в данном месте плохая связь</li>\r\n 	<li>Возможно Вы попали под временный фильтр Оператора, если некорректно использовали трафик</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n</ul>\r\n</div>\r\n<div class=\"my-accordion\">\r\n<h4 id=\"cant-make-call\"><b>Не получается сделать звонок:</b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>Необходимо правильно осуществлять набор номера (только в международном формате: +_код страны)</li>\r\n 	<li>Проверить баланс</li>\r\n 	<li>Перегрузить устройство</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n</ul>\r\n</div>\r\n<div class=\"my-accordion\">\r\n<h4 id=\"balance-not-replenished\"><b>Не пополняется баланс банковской картой на сайте оператора:</b></h4>\r\n</div>\r\n<div class=\"panel\">\r\n<ul>\r\n 	<li>Правильно указывайте свой номер «6ХХХХХХХХ», без +34</li>\r\n 	<li>Используйте для оплаты карту другого банка</li>\r\n 	<li>Пополняйте через наш сайт, курсовая разница ничтожна при таких суммах как 5 – 50 евро</li>\r\n 	<li>Позвонить в Service Desk: +34(695)91-19-00</li>\r\n 	<li>Написать на английском в Service Desk: contact@topup.orange.com (при этом надо указать тел. номер «6ХХХХХХХХ», Фамилию и Имя, указанные при регистрации, время и сумму платежа и приложить цветной сканированную копию загран. паспорта)</li>\r\n</ul>\r\n</div>\r\n[us_btn text=\"К содержанию\" link=\"url:%23full-description-content|||\" style=\"outlined\" size=\"14px\" align=\"center\" icon=\"fas|angle-up\" iconpos=\"right\" el_class=\"description-btn\"][us_separator type=\"invisible\" size=\"small\"]\r\n\r\n</div>\r\n<h3 id=\"faq\"><b>Вопросы и ответы: Orange Mundo</b></h3>\r\n<div class=\"row\">\r\n<div class=\"container\">\r\n<div class=\"wrapper maxheight\">\r\n<div class=\"full-description-block\">\r\n<div class=\"faq-block item\">\r\n<h4>Интересует sim-карта для поездки в Европу на авто (Польша, Чехия, Австрия, Германия).\r\nНужен интернет и голосовые звонки внутри Европы. Что посоветуете? И какие условия при переезде из страны в страну?</h4>\r\nДля стран ЕС лучший вариант - Orange.\r\n\r\nЕсть пакеты: MUNDO 1GB - 7 евро | MUNDO 2GB - 10 евро | MUNDO 3GB - 15 евро, которые часто подпадают под акции оператора по удвоению объема пакетов.\r\n\r\nДанные пакеты действуют по всей зоне стран входящий в список, т.е. при переезде из одной страны в другую страну пакеты не требуют дополнительного подключения.\r\n\r\nЗвонки в Европе:\r\n\r\nВходящие - бесплатно\r\n\r\nИсходящие - 0,08 €/ мин + плата за подключение, см. тариф\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Очень не плохая карта, но многих отталкивает самостоятельная активация карты и подключение пакетов.</h4>\r\nУ Вас неверная информация, это все делаем мы.\r\n\r\nВы получаете сим-карту, которая будет активирована и на ней будет подключен выбранный вами интернет-пакет в назначенную вами дату.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А Вы оказываете техническую поддержку или только продаете? Мне попадались интернет магазины, где сим-карты продают с 5 EUR балансом и все, дальше сами.</h4>\r\nДа, в интернет-магазинах Вы можете встретить сим-карты Orange Mundo без пакетов, с по Мб списанием и только с 5 EUR балансом, т.е. сначала надо пополнять баланс и потом самостоятельно подключать пакет через личный кабинет.\r\n\r\nМы оказываем следующие услуги:\r\n<ul>\r\n 	<li>продаем, с активацией у оператора</li>\r\n 	<li>подключаем тарифный план с выбранным пакетом,</li>\r\n 	<li>производим пополнение баланса купленных у нас сим-карт,</li>\r\n 	<li>даем необходимые рекомендации и инструкции</li>\r\n 	<li>осуществляем техническое сопровождение в течение первого месяца после активациисим-карты.</li>\r\n</ul>\r\nПокупая у нас, вы получите необходимый интернет без лишних хлопот.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А почему Вы осуществляете техническую поддержку только в течение первого месяца?</h4>\r\nПотому что сим-карты используют в течение первого месяца после активации. Абоненту становится очевидно, что заявленные услуги предоставляются и сим-карта работает. Далее, абонент пользуется сим-картой согласно инструкции, тарифам и ограничениям.\r\n\r\nЕсли клиенту важно, чтобы ему и в дальнейшем помогали по любым вопросам, то он может при покупке сим-карты взять услугу «Безлимитное сопровождение» за 10 EUR и тогда наши специалисты будут оказывать тех. поддержку в течение всего жизненного периода действия сим-карты. См. подробнее описание услуги.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Как получить \"Интернет пакет Mundo 4 ГБ за 10€ на 30 дней с учетом акции\"? Я еду через 10 дней после указанной даты окончания акции. Нужно активировать пакет не позднее этой даты?</h4>\r\nВы автоматически попадаете под акцию «Удвоение подключаемого пакета», если указываете дату активации, входящую в указанный промежуток, включая последний день акции.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А дату активации надо указывать при заказе сразу? И в Европе надо ли, еще какие то дополнительные действия по активации совершать?</h4>\r\nДа, сразу при оформлении заказа Вы указываете желаемую дату активации и необходимый интернет пакет.\r\n\r\nОбычно дату активации ставят за 1-2 дня до отъезда, не рекомендуем ставить дату на тот же день, когда уезжаете, иначе Вы можете быть уже за границей, а оператор произведет подключение только в течение этого дня.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Могу ли к своему смартфону, где используется карта Orange, подключить по WiFi планшет. И сколько устройств одновременно?</h4>\r\nРаздача разрешена Оператором.\r\n\r\nКоличество устройств подсоединенных к такой раздаче зависит от раздаваемой скорости вашего устройства, обычно пропускная входящая скорость гораздо выше, чем исходящая. Помните, что это все же мобильный интернет.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Если симкарту вставить в переносной 4G-wifi-роутер? И что со скоростью 4G?</h4>\r\nЭто будет лучший вариант для подключения интернета на несколько у строй, т.к. у 4G роутера передача будет в любом случае мощнее, чем у смартфона. Но если к вам подсоединяется 1 или 2 устройства, а смартфон не из дешевых, то его раздачи должно хватать.\r\n\r\nДля получения трафика на скорости 4G необходимо, чтобы страна, в которой Вы будете, входила в список стран, отмеченных значком 4G, а ваше устройство поддерживало 4G, при этом рекомендуем выбирать оператора по стране пребывания не автоматически, а вручную. На многих устройствах при выборе вручную появляется список операторов с отметкой 4G, иначе всю поездку вы можете использовать сеть оператора, который предоставляет только 3G.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Правильно я понял, что при возвращении из Европы надо будет перейти на другой тарифный план Llama для отмены ежемесячных платежей?</h4>\r\nПо окончании поездки:\r\n\r\n– Если на балансе карты остаток средств превышает стоимость пакета, который подключен на вашем тарифе Mundo, то рекомендуем Вам сменить тарифный план на Llama без абонентской платы.\r\n\r\n– Если баланс карты меньше стоимости подключенного интернет пакета, то Вы можете оставить карту с уже имеющимся тарифом.\r\n\r\nПомните, что бесплатно тариф меняется 5 раз, потом за оплату в 6 евро, поэтому если Вы планируете использовать карту долго в своих поездках, выбирайте первоначальный тариф по своим потребностям.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>М-мм, это не дорого?</h4>\r\nРоссийские операторы за каждую смену внутренних тарифов по инициативе Абонента снимают оплату, поэтому 5 раз бесплатно – это отличный вариант.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>И еще вопрос - до поездки в Европу возможно здесь проверить работоспособность сим-карты? Можно в России вставлять сим-карту в телефон и включать его для проверки? Деньги за регистрацию сим-карты в сетях РФ списываться не будут?</h4>\r\nВыход в интернет в странах не входящей в список стоит 12.10€ за 1 Мб. Если вы хотите посмотреть видит ли карта сеть, отключите в телефоне выход в интернет и iMessage на\r\n\r\niPhone, iPad если у вас эти устройства.\r\n\r\nЛучше используйте простые телефоны, при включение Вы получите СМС от оператора, что приветствуем в такой то стране, Ваш тариф такой то, если есть акция по удвоению пакета, то дополнительный объем также будет указан.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А что за проблемы у iMessage на iPhone, iPad?</h4>\r\niMessage и FaceTime - это сервисы Apple, которые не лишены определенных ошибок и одна из них, может разорить баланс вашей сим-карты. Этой ошибкой является регулярный запрос на активацию данных приложений.\r\n\r\nАктивация с обязательной отправкой СМС на британский номер +44 происходит как при первом включении, так и при замене сим-карты в iPhone и iPad.\r\n\r\nПри каждой такой попытке активации, происходит отправка SMS сообщения в Великобританию за 1,2 евро.\r\n\r\nБывает, что iMessage и FaceTime запрашивает активацию неоднократно, по нескольку раз в день. Поэтому если iMessage не отключить на вашем iPhone еще на российской сим-карте, у Вас может произойти списание с баланса и остаток на балансе стать отрицательным. А при 0 или минусовом балансе сим-карта Orange не видит сеть, даже при подключенном пакете.\r\n\r\nК сожалению, такие случаи не редки, т.к. многие пользователи Айфон не учитывают это при выборе пакета и баланса.\r\n\r\n<img class=\"size-medium wp-image-1308 alignnone\" src=\"https://orangesim.ru/wp-content/uploads/2018/03/download-169x300.jpg\" alt=\"\" width=\"169\" height=\"300\" />\r\n\r\nЕсли нажимать Ок, то СМС отправлен. Кстати, данные смс не отображаются и в стандартном приложении «Сообщения» их не видно.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А если я пополнил баланс на 5 евро. Проходит 11 месяцев, я потом опять пополняю на 5 евро и еще 11 она за мной? 5 евро, которые я не потратил, остаются на счету?</h4>\r\nДа, все верно\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Как начать пользоваться сим-картой через полгода, например? Сгорают ли оставшиеся гигабайты, если не успел потратить за месяц?</h4>\r\nДа, сгорают.\r\n\r\nНа карте действует тот тариф, который Вы подключали последним. Если он Вас не устраивает по объему трафика, подключите другой через личный кабинет.\r\n\r\nЕсли устраивает, то Вы должны просто пополнить баланс необходимый для оплаты пакета и после оплаты пакет сразу подключиться, т.е. у Вас на карте Мундо 1 Гб за 7 евро, а на счету 3 евро – пополняете на 5 евро и пакет подключается.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Хочу купить симку Orange 3 Гб. Что надо сделать?</h4>\r\nВыбираете купить, прикладываете копию заграничного паспорта и оплачиваете. Вам сразу высвечивается номер заказа. С этим номером вы можете забрать карту в любом пункте выдачи. Карты есть в наличии. Или мы сделаем доставку.\r\n\r\nПосле регистрации в сети Оператора, Вы получаете об уведомление на e-mail, после чего можно пользоваться интернетом, но только в странах действия тарифа.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>А могу я купить в офисе, где производиться выдача?</h4>\r\nНет. Там осуществляется именно выдача, наши партнеры не принимают денежные средства, иначе бы это привело к удорожанию конечной стоимости.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Например, у меня сим-карта с 20 EUR и был подключен пакет Мундо 3 ГБ. С баланса списалось 15 евро и остаток в 5 евро остается до следующей поездке или 11 месяцев до обязательного минимального пополнения? Пролонгации пакета на следующий месяц со списанием 15 евро и уходом в минус у меня не произойдет, т.к. остаток баланса меньше 15? Переходить на тариф LIama в таком случае не надо и через несколько месяцев я, дополнив баланс на 10 евро, выезжаю в Европу, и у меня будет подключен тот же тариф 3 Гб за 15 евро. Я правильно понимаю?</h4>\r\nДа, Вы все правильно поняли. У Вас, после пополнения на 10 евро, автоматом спишется 15 и подключится 3 Гб.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Добрый день! Уже есть карта Orange и удачный опыт поездки с ней. Собираюсь ехать еще в Европу и смотрю: Как пополнить баланс?</h4>\r\nПополнять можно через нас, т.к. суммы не большие, то и наценка небольшая, которая покрывает % от платежной системы Яндекс-касса и перевод рублей в евро. Можете пополнять самостоятельно через сайт Оператора.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Я приобрел две Сим карты orange mundo с активацией. Сейчас я в Белоруссии и дальше в Европу, но обе симки не работают. Прошу проверить в чем проблема. Симки оформлены на _________ тел __________</h4>\r\nЗдравствуйте! Обе Ваши сим-карты активны. Белоруссия не входит в зону действия пакета. Это не страна ЕС. Расход трафика в этой стране тарифицируется отдельно: 12,1 евро за 1 Мб. Если у Вас сим-карта находилась в устройстве в Беларуси, то оно могло для обновления использовать интернет без вашего прямого участия, а средства с баланса карт за использование трафика вне зоны ЕС списаться. Вы срочно отключите интернет и проверьте баланс, звонок на *111#\r\n\r\nСим-карта Orange с нулевым балансом не регистрируется в сети. Чтобы восстановилась работоспособность сим-карт, необходимо будет пополнить баланс до положительного.\r\n\r\n</div>\r\n<div class=\"faq-block item\">\r\n<h4>Доброго времени суток! Подскажите пожалуйста: При покупке карты Orange Mundo, необходимо указать дату активации карты. Мне нужно отправлять комбинацию слов, смс или за меня это все сделают, а я просто включаю телефон в Афинах, т.е. можно, прилетев на следующий день в Грецию, сразу включить и пользоваться )))? Я совсем запуталась ((.</h4>\r\nДа, все верно. В указанную дату карта в течение дня у Вас будет активирована автоматически и на ней начнет действовать подключенный пакет с интернетом.\r\n\r\n</div>\r\n</div>\r\n<div class=\"background\"></div>\r\n</div>\r\n</div>\r\n<button class=\"read-more-btn\">Читать больше (+)</button>\r\n\r\n</div>\r\n[/vc_column_text][vc_column_text][/vc_column_text][/vc_column][/vc_row][vc_row height=\"full\" valign=\"center\" color_scheme=\"secondary\" us_bg_image=\"1033\" us_bg_parallax=\"vertical\" us_bg_overlay_color=\"rgba(0,0,0,0.7)\" el_id=\"contact\"][vc_column animate=\"afb\"][vc_column_text]\r\n<h2 style=\"text-align: center;\"><strong>НАШИ <span class=\"highlight highlight_primary\">КОНТАКТЫ</span></strong></h2>\r\n[/vc_column_text][us_separator][vc_row_inner gap=\"20\"][vc_column_inner animate=\"afb\" width=\"1/2\"][us_iconbox icon=\"fas|phone\" style=\"outlined\" iconpos=\"left\" size=\"20px\"]<a href=\"tel:+74993220824\">+7(499)322-08-24 (Москва)</a>\r\n<a href=\"tel:+78123171825\">+7(812)317-18-25 (Санкт-Петербург)</a>[/us_iconbox][us_separator size=\"small\"][us_iconbox icon=\"fas|envelope\" style=\"outlined\" iconpos=\"left\" size=\"20px\" title=\"info@orangesim.ru\"][/us_iconbox][us_separator size=\"small\"][us_iconbox icon=\"material|access_time\" style=\"outlined\" iconpos=\"left\" size=\"20px\" title=\"Пн – Пт с 08:00 до 20:00\r\nСб – Вс с 10:00 до 18:00\"][/us_iconbox][us_separator size=\"small\"][us_iconbox icon=\"fas|map-marker-alt\" style=\"outlined\" iconpos=\"left\" size=\"20px\" title=\"101000, г. Москва, Колпачный пер, дом 6, строение 5\"]Офис в Москве работает только с корпоративными клиентами. Физические лица могут заказать сим-карту с доставкой или забрать в любом удобном офисе выдачи (сим-карты в наличии) после оформления на сайте.[/us_iconbox][/vc_column_inner][vc_column_inner animate=\"afb\" width=\"1/2\"][gravityform id=\"2\" title=\"false\" description=\"false\" ajax=\"true\"][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row height=\"auto\" width=\"full\"][vc_column][us_gmaps marker_address=\"101000, г. Москва, Колпачный пер, дом 6, строение 5\" marker_text=\"\" zoom=\"17\" latitude=\"55.758109\" longitude=\"37.641013\" markers=\"%5B%7B%7D%5D\"][vc_raw_html][/vc_raw_html][vc_raw_html][/vc_raw_html][/vc_column][/vc_row]', 'Главная', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2018-04-11 18:26:18', '2018-04-11 15:26:18', '', '5', 'https://orangesim.ru/bez-rubriki/5-revision-v1/', '0', 'revision', '', '0');
